import React from "react";
import "../styles/Header.css";

const Header = ({ walletAddress, onConnect, onDisconnect, menuItems }) => {
  const truncatedAddress = walletAddress
    ? `${walletAddress.slice(0, 6)}...${walletAddress.slice(-4)}`
    : "";

  return (
    <header className="header-container">
      <div className="logo">
        <span className="logo-highlight">Yo</span>Crush
      </div>
      <nav className="menu">
        {menuItems.map((item, index) => (
          <a key={index} href={`#${item.toLowerCase()}`} className="menu-item">
            {item}
          </a>
        ))}
      </nav>
      <div className="wallet-section">
        {walletAddress ? (
          <div
            className="wallet-display"
            onClick={onDisconnect}
            title="Disconnect Wallet"
          >
            <span className="wallet-icon"></span>
            <span className="wallet-address">{truncatedAddress}</span>
          </div>
        ) : (
          <button onClick={onConnect} className="connect-wallet-button">
            Connect Wallet
          </button>
        )}
      </div>
    </header>
  );
};

export default Header;
