            if (typeof window.ethereum !== "undefined") {
                try {
                    const accounts = await window.ethereum.request({ method: "eth_accounts" });
                    if (accounts && accounts.length > 0) {
                        console.log("Connected account after return:", accounts[0]);

                        // Store the connected account and clear interval
                        localStorage.setItem("connectedAccount", accounts[0]);
                        clearInterval(interval);

                        // Handle connected account
                        alert(`Connected to account: ${accounts[0]}`);
                    }
                } catch (err) {
                    console.error("Error checking MetaMask accounts:", err);
                }
            } else {
                console.warn("Waiting for MetaMask to be installed.");
            }
        }, 2000);
    }
