import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import { ethers } from "ethers";
import WalletConnectProvider from "@walletconnect/web3-provider";
import WalletModal from "./WalletModal";
import ABI from "../abi.json";

const LoadingSpinner = () => {
  return (
    <div className="loading-overlay">
      <div className="spinner"></div>
    </div>
  );
};

const NFTCards = () => {
  const [nfts, setNfts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [gameStarted, setGameStarted] = useState(false);
  const [rankings, setRankings] = useState({});
  const [availableRanks, setAvailableRanks] = useState([]);
  const [walletAddress, setWalletAddress] = useState("");
//   const [error, setError] = useState("");
//   const [isModalOpen, setIsModalOpen] = useState(false);
  const [leaderboard, setLeaderboard] = useState([]);

  const backendUrl = "http://127.0.0.1:3001"; // Replace with your backend API URL
  const contractAddress = "0xC1cCeb5adFE832bb5788Db8F10E8b083C037c89b"; // Replace with your contract address
  const customRpcUrl = "https://rpc2.bahamut.io"; // Replace with your custom RPC URL

  const provider = new ethers.JsonRpcProvider(customRpcUrl);
  const hasInitialized = useRef(false);

  useEffect(() => {
    const storedWalletAddress = localStorage.getItem("connectedAccount");
    if (storedWalletAddress) {
      setWalletAddress(storedWalletAddress);
      console.log("Restored wallet address from storage:", storedWalletAddress);
    }
  }, []);

  const handleLogout = () => {
    setWalletAddress(null);
    localStorage.removeItem("connectedAccount");
    setError("");
    setIsModalOpen(false);
    console.log("User disconnected.");
  };

  const startNewGame = async () => {
    if (loading || hasInitialized.current) return;
    console.log("startNewGame called");
    hasInitialized.current = true;
    setLoading(true);
    setGameStarted(true);

    try {
      const response = await axios.get(backendUrl);
      console.log("ðŸš€ ~ startNewGame ~ response:", response);
      const parsedNFTs = response.data.nfts.map((nft) => ({
        id: nft.id,
        metadata: JSON.parse(nft.metadata),
      }));
      setNfts(parsedNFTs);

      await fetchLeaderboard(parsedNFTs);

      setRankings({});
      setAvailableRanks([...Array(parsedNFTs.length).keys()].map((i) => i + 1));
    } catch (error) {
      console.error("Error fetching NFTs:", error);
    } finally {
      setLoading(false);
    }
  };

  const fetchLeaderboard = async () => {
    try {
      const contract = new ethers.Contract(contractAddress, ABI, provider);

      const rawData = await contract.getLeaderboard(1);
      const leaderboardData = rawData
        .map((entry) => ({
          rank: Number(entry[0]),
          score: Number(entry[1]),
        }))
        .filter((entry) => entry.rank !== 0);

      const leaderboardWithNames = await Promise.all(
        leaderboardData.map(async (entry) => {
          try {
            const response = await axios.get(`${backendUrl}/nft/${entry.rank}`);
            const nftName = response.data.name;
            return { ...entry, name: nftName };
          } catch (error) {
            console.error(`Error fetching NFT name for ID ${entry.rank}:`, error);
            return { ...entry, name: "Unknown" };
          }
        })
      );

      setLeaderboard(leaderboardWithNames);
    } catch (err) {
      console.error("Error fetching leaderboard:", err);
    }
  };

  const handleSubmitRanks = async () => {
    if (!walletAddress) {
      setError("Please connect your wallet first.");
      return;
    }

    if (Object.keys(rankings).length !== nfts.length) {
      setError("Please assign ranks to all NFTs.");
      return;
    }

    const ids = Object.entries(rankings)
      .sort(([, rankA], [, rankB]) => rankA - rankB)
      .map(([index]) => {
        const nft = nfts[Number(index)];
        if (!nft || !nft.id) {
          throw new Error(`Missing or undefined ID for NFT at index ${index}`);
        }
        return nft.id;
      });

    try {
      setLoading(true);
      setError("");

      const provider = new ethers.BrowserProvider(window.ethereum);
      const signer = await provider.getSigner();
      const contract = new ethers.Contract(contractAddress, ABI, signer);

      console.log("Submitting rankings:", ids);
      const tx = await contract.submit(ids);
      await tx.wait();

      alert("Rankings submitted successfully!");

      fetchNewGameData();
    } catch (err) {
      console.error("Error submitting rankings:", err);
      setError("Failed to submit rankings. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  const fetchNewGameData = async () => {
    try {
      setLoading(true);

      const response = await axios.get(backendUrl);
      const parsedNFTs = response.data.nfts.map((nft) => ({
        id: nft.id,
        metadata: JSON.parse(nft.metadata),
      }));

      setNfts(parsedNFTs);

      setRankings({});
      setAvailableRanks([...Array(parsedNFTs.length).keys()].map((i) => i + 1));

      await fetchLeaderboard();

      console.log("Game reset complete.");
    } catch (err) {
      console.error("Error fetching new game data:", err);
      setError("Failed to fetch new game data. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  const handleAssignRank = (index, rank) => {
    setRankings((prev) => ({ ...prev, [index]: rank }));
    setAvailableRanks((prev) => prev.filter((r) => r !== rank));
  };

//   useEffect(() => {
//     if (typeof window.ethereum !== "undefined") {
//       window.ethereum.on("accountsChanged", (accounts) => {
//         if (accounts.length > 0) {
//           setWalletAddress(accounts[0]);
//         } else {
//           handleLogout();
//         }
//       });

//       window.ethereum.on("chainChanged", () => {
//         window.location.reload();
//       });
//     }
//   }, []);

  useEffect(() => {
    startNewGame();
  }, []);

  return (
    <div className="nft-cards-container">
      {loading && <LoadingSpinner />}
      {!loading && (
        <>
          <div className="cards-wrapper">
            {nfts.map((nft, index) => (
              <div
                className={`nft-card ${rankings[index] ? "inactive-card" : ""}`}
                key={index}
                style={{
                  opacity: rankings[index] ? 0.5 : 1,
                  pointerEvents: rankings[index] ? "none" : "auto",
                }}
              >
                <h3>{nft.metadata.name || `NFT ${index + 1}`}</h3>
                <img src={nft.metadata.image} alt={nft.metadata.name || "NFT"} />
                <div className="rank-buttons">
                  {!rankings[index] &&
                    availableRanks.map((rank) => (
                      <button
                        key={rank}
                        onClick={() => handleAssignRank(index, rank)}
                        disabled={
                          rankings[index] === rank ||
                          !!Object.values(rankings).find((r) => r === rank)
                        }
                      >
                        {`Rank ${rank}`}
                      </button>
                    ))}
                </div>
                {rankings[index] && <p>Assigned Rank: {rankings[index]}</p>}
              </div>
            ))}
          </div>
          <button onClick={handleSubmitRanks} className="submit-ranks-button">
            Submit Rankings
          </button>
          <div className="leaderboard-container">
            <h2>Leaderboard</h2>
            <table className="leaderboard-table">
              <thead>
                <tr>
                  <th>Id</th>
                  <th>Name</th>
                  <th>Score</th>
                </tr>
              </thead>
              <tbody>
                {leaderboard.map((entry, index) => (
                  <tr key={index}>
                    <td>{entry.rank}</td>
                    <td>{entry.name}</td>
                    <td>{entry.score}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </>
      )}
    </div>
  );
};

export default NFTCards;
