import React from "react";
import walletConnectLogo from "../assets/walletconnect-logo.png"; // Add appropriate logo path
import "./WalletModal.css"; // Add CSS for styling

const WalletModal = ({ isOpen, onClose, onConnectMetaMask, onConnectWalletConnect }) => {
    if (!isOpen) return null;

    return (
        <div className="modal-overlay">
            <div className="modal">
                <h2>Connect Wallet</h2>
                <div className="wallet-options">
                    <button className="wallet-button" onClick={onConnectMetaMask}>
                        <img src="/images/metamask_logo.png" alt="MetaMask" className="wallet-logo" />
                        MetaMask
                    </button>
                    <button className="wallet-button" onClick={onConnectWalletConnect}>
                        <img src="/images/WalletConnect-Logo.png" alt="WalletConnect" className="wallet-logo" />
                        WalletConnect
                    </button>
                </div>
                <button onClick={onClose} className="modal-close">
                    Cancel
                </button>
            </div>
        </div>
    );
};

export default WalletModal;
